import React, { useMemo, useState } from 'react';
import api from '../api';
import { motion, AnimatePresence } from 'framer-motion';
import { Link } from 'react-router-dom';
import StudentInfoCard from '../components/StudentInfoCard';
import '../components/StudentInfoCard.css';
import './MentorDashboard.css';

export default function MentorDashboard() {
  const [resource, setResource] = useState('');
  const [status, setStatus] = useState('');
  const [error, setError] = useState('');
  const mentorEmail = localStorage.getItem('email') || '';
  const [students, setStudents] = useState([]);
  const [messages, setMessages] = useState({}); // map studentEmail -> message
  const [viewItem, setViewItem] = useState(null);
  const [messageItem, setMessageItem] = useState(null);
  const [messageText, setMessageText] = useState('');
  const [aiData, setAiData] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [loadingAi, setLoadingAi] = useState(false);
  const [selectedStudent, setSelectedStudent] = useState(null);
  const [downloading, setDownloading] = useState(false);

  const onSubmit = async (e) => {
    e.preventDefault();
    setStatus('');
    setError('');
    try {
      const res = await api.post('/api/mentors/sendResource', { mentorEmail, resource });
      setStatus(`Resource sent. Total resources: ${res.data.data?.resources?.length || 0}`);
      setResource('');
    } catch (err) {
      setError(err?.response?.data?.message || 'Failed to send');
    }
  };

  React.useEffect(() => {
    const load = async () => {
      try {
        let resp;
        try {
          if (mentorEmail) {
            resp = await api.get('/api/mentor/students', { params: { email: mentorEmail } });
          } else {
            resp = await api.get('/api/mentor/students');
          }
        } catch (e1) {
          try {
            if (mentorEmail) {
              resp = await api.get('/api/mentors/students', { params: { email: mentorEmail } });
            } else {
              resp = await api.get('/api/mentors/students');
            }
          } catch (e2) {
            // fallback to teacher listing to guarantee data is visible
            resp = await api.get('/api/teacher/students');
          }
        }
        setStudents(resp.data || []);
      } catch (err) {
        setError(err?.response?.data?.message || 'Failed to load students');
      }
    };
    load();
  }, [mentorEmail]);

  const sendIntervention = async (studentEmail) => {
    try {
      const message = messages[studentEmail] || '';
      if (!message) return setError('Please enter a message');
      await api.post('/api/mentor/intervention', { studentEmail, mentorEmail, message });
      setStatus('Intervention sent');
      setMessages((m) => ({ ...m, [studentEmail]: '' }));
    } catch (err) {
      setError(err?.response?.data?.message || 'Failed to send intervention');
    }
  };

  const getRiskLevel = (s) => {
    let risk = 0;
    if ((s.attendance ?? 100) < 75) risk += 2;
    if ((s.cgpa ?? 10) < 6) risk += 2;
    if ((s.financialScore ?? 100) < 50) risk += 1;
    if ((s.studyHours ?? 10) < 2) risk += 2;
    if ((s.previousYearBacklogs ?? 0) > 2) risk += 2;
    if (risk >= 6) return 'High';
    if (risk >= 3) return 'Moderate';
    return 'Low';
  };

  const getRiskColor = (level) => {
    if (level === 'High') return '#f87171';
    if (level === 'Moderate') return '#facc15';
    return '#4ade80';
  };

  const highRisk = useMemo(() => students.filter((s) => getRiskLevel(s) === 'High'), [students]);
  const others = useMemo(() => students.filter((s) => getRiskLevel(s) !== 'High'), [students]);

  const handleDownloadReport = async (student) => {
    try {
      setDownloading(true);
      const response = await api.post('/api/ai/download-report', {
        student: {
          name: student.name,
          usn: student.usn || student.USN || 'N/A',
          email: student.email,
          attendance: student.attendance || 0,
          cgpa: student.cgpa || 0,
          financial_score: student.financialScore || 0,
          study_hours: student.studyHours || 0,
          previous_year_backlogs: student.previousYearBacklogs || 0
        }
      }, { responseType: 'blob' });

      const fileURL = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = fileURL;
      link.setAttribute('download', `${student.usn || student.USN || 'student'}_report.pdf`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      console.error("Download failed", err);
      setError("Failed to download report. Please try again.");
    } finally {
      setDownloading(false);
    }
  };

  const counseling = async (s) => {
    try {
      await api.post('/api/mentor/intervention', { studentEmail: s.email, mentorEmail, message: `Counseling recommended for ${s.name} (${s.usn}).` });
      setStatus('Counseling note sent');
    } catch (err) {
      setError(err?.response?.data?.message || 'Failed to send counseling note');
    }
  };

  const handleAiSuggestions = async (student) => {
    try {
      setSelectedStudent(student);
      setLoadingAi(true);
      setError('');

      console.log('Student data:', student);

      // Prepare request data with correct field names
      const requestData = {
        attendance: parseFloat(student.attendance) || 0,
        cgpa: parseFloat(student.cgpa) || 0,
        financial_score: parseFloat(student.financialScore || student.financial_score) || 0,
        study_hours: parseFloat(student.studyHours || student.study_hours) || 0,
        previous_year_backlogs: parseInt(student.previousYearBacklogs || student.previous_year_backlogs) || 0
      };

      console.log('Sending AI prediction request:', requestData);

      // Use the API instance which should already have the base URL and auth headers set
      const response = await api.post('/api/ai/predict', requestData);

      console.log('AI prediction response:', response.data);

      setAiData(response.data);
      setShowModal(true);

      // Download the PDF after showing the modal
      await downloadPdf(student, response.data);

    } catch (err) {
      console.error('AI Suggestions Error:', err);
      console.error('Error details:', err.response?.data || err.message);
      setError('Failed to get AI suggestions. Please check console for details.');
    } finally {
      setLoadingAi(false);
    }
  };

  // Function to handle PDF download
  const downloadPdf = async (student, aiData) => {
    try {
      console.log('Starting PDF download for student:', student.name);

      // Get the JWT token from localStorage
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('No authentication token found. Please log in again.');
      }

      // Prepare the data to send
      const data = {
        student: {
          ...student,
          attendance: student.attendance || 0,
          cgpa: student.cgpa || 0,
          studyHours: student.studyHours || 0,
          previousYearBacklogs: student.previousYearBacklogs || 0,
          financialScore: student.financialScore || 0
        },
        risk: aiData.risk,
        score: aiData.score,
        tips: aiData.tips
      };

      // Use the full URL to avoid any path resolution issues
      const response = await fetch('http://localhost:5000/api/ai/generate-pdf', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/pdf',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(data),
        credentials: 'include' // Include cookies if using them for auth
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`Server responded with status ${response.status}: ${errorText}`);
      }

      // Create a blob from the response
      const blob = await response.blob();

      // Create a URL for the blob
      const url = window.URL.createObjectURL(blob);

      // Create a temporary anchor element
      const link = document.createElement('a');
      link.href = url;
      link.download = `${student.name.replace(/\s+/g, '_')}_suggestions.pdf`;

      // Append to the document and trigger download
      document.body.appendChild(link);
      link.click();

      // Clean up
      setTimeout(() => {
        window.URL.revokeObjectURL(url);
        document.body.removeChild(link);
      }, 100);

    } catch (error) {
      console.error('Error downloading PDF:', error);
      setError('Failed to download PDF. ' + error.message);
    }
  };

  const closeModal = () => {
    setShowModal(false);
    setAiData(null);
    setSelectedStudent(null);
  };

  return (
    <motion.div initial={{ x: 24, opacity: 0 }} animate={{ x: 0, opacity: 1 }} transition={{ duration: 0.4 }}>
      <h3 style={{ marginTop: 0 }}>Mentor Dashboard</h3>
      <div className="dashboard-layout">
        <aside className="glass-card sidebar">
          <Link className="side-link" to="/mentor"><span className="icon">ðŸ </span>Home</Link>
          <Link className="side-link" to="/mentor"><span className="icon">âœ‰ï¸</span>Messages</Link>
          <Link className="side-link" to="/profile"><span className="icon">ðŸ‘¤</span>Profile</Link>
        </aside>
        <section>
          <div className="glass-card">
            <p>Send a resource/message to your assigned students.</p>
            <form onSubmit={onSubmit} className="form-col">
              <input type="text" value={mentorEmail} readOnly />
              <input type="text" placeholder="Resource link or message" value={resource} onChange={(e) => setResource(e.target.value)} />
              <button className="btn" type="submit">Send</button>
              {status && <p className="success">{status}</p>}
              {error && <p className="error">{error}</p>}
            </form>
          </div>

          <div className="glass-card" style={{ marginTop: 12 }}>
            <h4 style={{ marginTop: 0 }}>âš ï¸ High Risk Students</h4>
            {highRisk.length === 0 ? (
              <p>No high risk students</p>
            ) : (
              <ul className="list">
                {highRisk.map((s) => (
                  <li key={s._id} className="list-item" style={{ alignItems: 'flex-start', gap: 8 }}>
                    <div className="student-info">
                      <div className="student-name"><b>{s.name}</b> â€” {s.usn}</div>
                      <div className="student-email">{s.email}</div>
                      <div className="student-stats">CGPA {s.cgpa} â€¢ Attendance {s.attendance}%</div>
                      <div style={{ marginTop: 4 }}>
                        <span className="badge" style={{ background: getRiskColor('High') }}>High</span>
                      </div>
                    </div>
                    <div style={{ minWidth: 320 }}>
                      <input
                        className="intervention-input"
                        placeholder="Intervention / message"
                        value={messages[s.email] || ''}
                        onChange={(e) => setMessages((m) => ({ ...m, [s.email]: e.target.value }))}
                      />
                      <div style={{ display: 'flex', gap: 6, marginTop: 6 }}>
                        <button className="btn" onClick={() => sendIntervention(s.email)}>ðŸ’¬ Intervention</button>
                        <button className="btn" onClick={() => setMessageItem(s)}>ðŸ“© Message</button>
                        <button className="btn" onClick={() => setViewItem(s)}>ðŸ“˜ Info</button>
                        <button className="btn" onClick={() => alert('Marks module in progress')}>ðŸ“ˆ Marks</button>
