import React, { useEffect, useState } from 'react';
import api from '../api';

export default function StudentDashboard() {
  const email = localStorage.getItem('email') || '';
  const [student, setStudent] = useState(null);
  const [resources, setResources] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const run = async () => {
      setError('');
      try {
        if (!email) return setError('No email in localStorage. Please login.');
        const s = await api.get(`/api/students/${encodeURIComponent(email)}`);
        setStudent(s.data.data);
        const r = await api.get(`/api/mentors/resourcesForStudent`, { params: { studentEmail: email } });
        setResources(r.data.data || []);
      } catch (err) {
        setError(err?.response?.data?.message || 'Failed to load');
      }
    };
    run();
  }, [email]);

  return (
    <div>
      <h3>Student Dashboard</h3>
      {error && <p className="error">{error}</p>}
      {!student ? (
        <div className="card"><p>Loading...</p></div>
      ) : (
        <div className="card">
          <h4>{student.name}</h4>
          <p>Email: {student.email}</p>
          <p>USN: {student.usn}</p>
          <p>CGPA: {student.cgpa}</p>
          <p>Attendance: {student.attendance}%</p>
          <p>Mentor: {student.mentorEmail || '—'}</p>
          <p>Class Teacher: {student.classTeacherEmail || '—'}</p>
        </div>
      )}

      <div className="card">
        <h4>Mentor Resources</h4>
        {resources.length === 0 ? (
          <p>No resources yet</p>
        ) : (
          <ul className="list">
            {resources.map((r, idx) => (
              <li key={idx} className="list-item">
                <span>{r}</span>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}
