                  <ul style={{ margin: 0, paddingLeft: '20px' }}>
                    {suggestion.tips.map((tip, index) => (
                      <li key={index} style={{ marginBottom: '8px', lineHeight: '1.4' }}>
                        <span style={{
                          display: 'inline-flex',
                          alignItems: 'center',
                          justifyContent: 'center',
                          width: '20px',
                          height: '20px',
                          borderRadius: '50%',
