import React, { useState } from 'react';
import Papa from 'papaparse';
import api from '../api';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';

export default function TeacherDashboard() {
  const [file, setFile] = useState(null);
  const [result, setResult] = useState('');
  const [error, setError] = useState('');

  const onFileChange = (e) => {
    setFile(e.target.files?.[0] || null);
    setResult('');
    setError('');
  };

  const parseAndUpload = () => {
    if (!file) return setError('Please choose a CSV file');
    setError('');
    Papa.parse(file, {
      header: true,
      skipEmptyLines: true,
      complete: async (res) => {
        try {
          const students = res.data.map((row) => ({
            name: row.name || row.Name,
            email: (row.email || row.Email || '').toLowerCase(),
            usn: row.usn || row.USN,
            cgpa: Number(row.cgpa || row.CGPA || 0),
            attendance: Number(row.attendance || row.Attendance || 0),
            mentorEmail: row.mentorEmail || row.MentorEmail || '',
            classTeacherEmail: row.classTeacherEmail || row.ClassTeacherEmail || '',
          })).filter((s) => s.name && s.email && s.usn);

          if (students.length === 0) return setError('No valid rows found');

          const resp = await api.post('/api/students/bulkAdd', { students });
          setResult(`Inserted: ${resp.data.inserted}`);
        } catch (err) {
          setError(err?.response?.data?.message || 'Upload failed');
        }
      },
      error: () => setError('Failed to parse CSV'),
    });
  };

  return (
    <motion.div initial={{ x: 24, opacity: 0 }} animate={{ x: 0, opacity: 1 }} transition={{ duration: 0.4 }}>
      <h3 style={{ marginTop: 0 }}>Teacher Dashboard</h3>
      <div className="dashboard-layout">
        <aside className="glass-card sidebar">
          <Link className="side-link" to="/teacher"><span className="icon">üè†</span>Home</Link>
          <Link className="side-link" to="/teacher"><span className="icon">üì•</span>Bulk Upload</Link>
          <Link className="side-link" to="/profile"><span className="icon">üë§</span>Profile</Link>
        </aside>
        <section>
          <div className="glass-card">
            <p>Upload CSV of students with columns: <b>name, email, usn, cgpa, attendance, mentorEmail, classTeacherEmail</b></p>
            <input type="file" accept=".csv" onChange={onFileChange} />
            <div style={{ marginTop: 8 }}>
              <button className="btn" onClick={parseAndUpload}>Upload</button>
            </div>
            {result && <p className="success" style={{ marginTop: 8 }}>{result}</p>}
            {error && <p className="error" style={{ marginTop: 8 }}>{error}</p>}
          </div>
        </section>
      </div>
    </motion.div>
  );
}
